import React from 'react'
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const StackedBarChart = (props) => {
    const { data, title, styleChart, styleTitle } = props

    const handleData = ({data}) => {
        return (
            data[0].barValue.map((value) => {
                return (
                    <Bar dataKey={value.dataKey} stackId={value.dataKey} fill={value.fill} />
                )
            })
        )
    }

    return (
        <div
            style={styleChart}
        >
            <p style={styleTitle}>{title}</p>
            <ResponsiveContainer>
                <BarChart
                    width={1600}
                    height={600}
                    data={data}
                    margin={{
                        top: 20,
                        right: 60,
                        left: 20,
                        bottom: 0,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />

                    {/* <Bar dataKey="totalCalls" stackId="a" fill="#00994C" />
                    <Bar dataKey="answeredCalls" stackId="a" fill="#82ca9d" />
                    <Bar dataKey="missedCalls" stackId="a" fill="#00FF80" />
                    <Bar dataKey="abandonedCalls" stackId="a" fill="#FF9933" />
                    <Bar dataKey="shortAbandonedCalls" stackId="a" fill="#CC0000" />
                    <Bar dataKey="voicemail" stackId="a" fill="#006633" /> */}
                </BarChart>
            </ResponsiveContainer>

        </div>
    )
}

export default StackedBarChart
